export const MIME_TYPE_FOLDER = "application/vnd.google-apps.folder";
export class FileInfo {
  static fromGoogleFile(file) {
    let fileInfo = new FileInfo();
    fileInfo.Id = file.id;
    fileInfo.MimeType = file.mimeType;
    fileInfo.ModifiedTime = new Date(file.modifiedTime);
    fileInfo.Name = file.name;
    fileInfo.Size = file.size;
    return fileInfo;
  }

  get Icon() {
    if (this.IsFolder) {
      return "folder";
    } else {
      return "file";
    }
  }

  get IsFolder() {
    return this.MimeType === MIME_TYPE_FOLDER;
  }

  get ModifiedTimeText() {
    return this.ModifiedTime.getDate() + "." + (this.ModifiedTime.getMonth() + 1) + "." + this.ModifiedTime.getFullYear();
  }

  get SizeText() {
    if (!this.Size) return "-";
    let size = parseInt(this.Size);
    if (size < Math.pow(1024, 1)) return size.toString();else if (size < Math.pow(1024, 2)) return Math.floor(size / Math.pow(1024, 1)) + " KB";else if (size < Math.pow(1024, 3)) return Math.floor(size / Math.pow(1024, 2)) + " MB";else if (size < Math.pow(1024, 3)) return Math.floor(size / Math.pow(1024, 3)) + " GB";else return Math.floor(size / Math.pow(1024, 4)) + " GB";
  }

}
