      if (Id) {
        activeModal.componentInstance.parentId = Id;
      } else {
        activeModal.componentInstance.parentId = this.materialGroupId;
      }
    }

    activeModal.result.then(result => {
      if (result) {
        this.searchMaterialGroup();
      }
    }, reason => {});
  }

  deleteMaterialGroup(Id) {
    this.materialGroupService.deleteMaterialGroup({
      Id: Id,
      LogUserId: this.logUserId
    }).subscribe(data => {
      //this.check=true;
      this.modelMaterialGroup.Id = '';
      this.searchMaterialGroup();
      this.messageService.showSuccess('Xóa nhóm nhóm vật tư thành công!');
    }, error => {
      this.messageService.showError(error);
    });
  }

  searchMaterialGroupById(Id) {
    this.materialGroupService.searchMaterialGroup(this.modelMaterialGroup).subscribe(data => {
      if (data) {
        this.listDAById = data;
        this.modelMaterialGroup.TotalItems = data.TotalItem;

        if (this.listDAById.length == 1) {
          this.deleteMaterialGroup(Id);
        } else {
          this.messageService.showConfirm("Xóa nhóm vật tư này sẽ xóa hết cả các nhóm vật tư con thuộc nhóm, Bạn có chắc chắn muốn xóa không?").then(data => {
            this.deleteMaterialGroup(Id);
          }, error => {});
        }
      }
    }, error => {
      this.messageService.showError(error);
    });
  }

  showConfirmDeleteMaterialGroup(Id) {
