  ChooseMaterial() {
    this.materials = [];

    if (this.modelMechanical.ProductCode == "") {
      this.messageService.showMessage("Bạn phải nhập mã Module");
      return;
    }

    let activeModal = this.modalService.open(ChooseMaterialForTemplateComponent, {
      container: 'body',
      windowClass: 'choose-material-template',
      backdrop: 'static'
    });
    var ListIdSelect = [];
    activeModal.componentInstance.ProductCode = this.modelMechanical.ProductCode; //this.modelMechanical.ProductCode = this.ProductCode;

    this.materials.forEach(element => {
      ListIdSelect.push(element.Id);
    });
    activeModal.componentInstance.ListIdSelect = ListIdSelect;
    activeModal.result.then(result => {
      if (result && result.length > 0) {
        result.forEach(element => {
          this.materials.push(element);
        });

        if (this.materials.length > 0) {
          this.GeneralPreliminaryEstimate();
        }
      }
    }, reason => {});
  }

